import Link from 'next/link';
import { GetServerSideProps } from 'next';

import { getPostsData } from '../lib/api';

import { dellPost } from '../lib/api';
import styled from 'styled-components';

import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
    root: {
        maxWidth: 345,
    },
    media: {
        height: 140,
    },
});

{
    /* <Post key={id}>
                            <PostContent>
                                <Title>
                                    <div>{title}</div>
                                </Title>

                                <BodyPost>{body}</BodyPost>

                                <Comment>
                                    {comments && comments.body !== ' ' && comments.body !== ''
                                        ? comments.body
                                        : 'no comment'}
                                </Comment>
                            </PostContent>
                            <div>
                                <BtnDel onClick={dellPost.bind(this, id)}>DELETE</BtnDel>
                            </div>
                        </Post> */
}

const Posts = ({ allPostsData }) => {
    const classes = useStyles();

    return (
        <div>
            <Grid>
                {allPostsData.map(({ id, body, title, comments }) =>
                    id && id !== ' ' && body && body !== ' ' && title && title !== ' ' ? (
                        <Card className={classes.root}>
                            <CardActionArea>
                                <CardMedia
                                    className={classes.media}
                                    image="/static/images/cards/contemplative-reptile.jpg"
                                    title="Contemplative Reptile"
                                />
                                <CardContent>
                                    <Typography gutterBottom variant="h5" component="h2">
                                        {title}
                                    </Typography>
                                    <Typography variant="body2" color="textSecondary" component="p">
                                        {body}
                                    </Typography>
                                </CardContent>
                            </CardActionArea>
                            <CardActions>
                                <Button size="small" color="primary">
                                    Share
                                </Button>
                                <Button size="small" color="primary">
                                    Learn More
                                </Button>
                            </CardActions>
                        </Card>
                    ) : null,
                )}
            </Grid>
        </div>
    );
};

const Grid = styled.div`
    /* display: grid;
    grid-template-rows: 1fr 1fr 1fr;
    grid-template-columns: 31% 31% 31%;
    grid-gap: 2vw; */

    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    justify-content: space-between;
`;

const Post = styled.div`
    background: #ddd;
    border-radius: 3px;
    padding: 15px;
    cursor: pointer;
    transition: box-shadow 0.1s;
    overflow: hidden;
    position: relative;

    :hover {
        box-shadow: 0 0 5px rgba(0, 0, 0, 0.5);
    }
`;

const PostContent = styled.div`
    padding-bottom: 120px;
`;

const Comment = styled.div`
    margin-top: 50px;
    border-radius: 3px;
    padding: 5px 10px;
    background: #cacaca;

    position: absolute;
    bottom: 70px;
    left: 10%;
    right: 10%;
`;

const Title = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;

    font-size: 1.5rem;
    margin-bottom: 10px;
`;

const BtnDel = styled.div`
    color: #fff;
    background-color: rgb(220, 0, 78);
    margin: 8px;
    box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14),
        0px 1px 5px 0px rgba(0, 0, 0, 0.12);
    margin-bottom: 10px;
    text-align: center;
    position: absolute;
    bottom: 10px;
    left: 8%;

    padding: 6px 16px;
    font-size: 0.875rem;
    min-width: 64px;
    width: 80%;
    box-sizing: border-box;
    transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms,
        box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms, border 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms;
    font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif;
    font-weight: 500;
    line-height: 1.75;
    border-radius: 4px;
    letter-spacing: 0.02857em;
    text-transform: uppercase;
    cursor: pointer;

    :hover {
        background-color: rgb(154, 0, 54);
        box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.2), 0px 4px 5px 0px rgba(0, 0, 0, 0.14),
            0px 1px 10px 0px rgba(0, 0, 0, 0.12);
    }
`;

const BodyPost = styled.div``;

export const getServerSideProps: GetServerSideProps = async (context) => {
    const allPostsData = await getPostsData();
    return {
        props: {
            allPostsData,
        },
    };
};

export default Posts;
