import styled from "styled-components";

const Layout = ({ children }) => {
  return <Container>{children}</Container>;
};

const Container = styled.div`
  color: #333;
  background: yellow;

  max-width: 36rem;
  padding: 0 1rem;
  margin: 3rem auto 6rem;
`;

export default Layout;
