import { getAllPostIds, getPostData } from '../../lib/api';

const Post = ({ postData }) => {
    return (
        <Layout>
            <Post>
                <PostLogo src="/images/post-logo.png" alt="React-logo" />
                <PostContent>
                    <Title>
                        <div>{postData.title}</div>
                    </Title>

                    <BodyPost>{postData.body}</BodyPost>

                    <Comment>
                        {postData.comments && postData.comments.body !== ' ' && postData.comments.body !== ''
                            ? postData.comments.body
                            : 'no comment'}
                    </Comment>
                </PostContent>
                <div>
                    <BtnDel onClick={dellPost.bind(this, id)}>DELETE</BtnDel>
                </div>
            </Post>
        </Layout>
    );
};

export async function getStaticPaths() {
    const paths = getAllPostIds();
    return {
        paths,
        fallback: false,
    };
}

export async function getStaticProps({ params }) {
    const postData = getPost(params.id);
    return {
        props: {
            postData,
        },
    };
}

export default Post;
