import * as axios from 'axios';

/* const instanse = axios.create({
    withCredentials: true,
    
}); */

const apiClient = axios.create({
    baseURL: `https://simple-blog-api.crew.red/posts/`,
    responseType: 'json',
    headers: {
        'Content-Type': 'application/json',
    },
});

export async function getPostsData() {
    const res = await apiClient.get();
    const data = await res.data;

    return data;
}

export async function dellPost(id) {
    const res = await apiClient.delete(`${id}`);
}

export async function addPost({ title, body }) {
    const res = await axios.post(`https://simple-blog-api.crew.red/posts`, { title: title, body: body });
}
