import Layout from '../../components/layout';

import { Formik, FormikHelpers, FormikProps, Form, Field, FieldProps } from 'formik';

import Button from '@material-ui/core/Button';
import styled from 'styled-components';

interface MyFormValues {
    message: string;
}

export const CreatePost: React.FC<{}> = () => {
    const initialValues: MyFormValues = { message: '' };
    return (
        <Layout home={false}>
            <h1>Create new post</h1>
            <Formik
                initialValues={initialValues}
                onSubmit={(values, actions) => {
                    console.log({ values, actions });
                    alert(JSON.stringify(values, null, 2));
                    actions.setSubmitting(false);
                }}
                render={({ isSubmitting }) => (
                    <Form>
                        <MyStyledForm>
                            <Field wrap="off" name="message" as={MyStyledTextarea} className="form-input" />
                            <BtnForm>
                                <Button type="submit" disabled={isSubmitting} variant="contained" color="primary">
                                    Create
                                </Button>
                            </BtnForm>
                        </MyStyledForm>
                    </Form>
                )}
            />
        </Layout>
    );
};

const MyStyledInput = styled.input`
    padding: 0.5em;
    border: 1px solid #ccc;
    overflow: auto;
    outline: none;
    -moz-appearance: none;
    resize: none;
    height: 150px;
    font-size: 1.1rem;
`;

const MyStyledForm = styled.div`
    display: flex;
    flex-direction: column;
`;

const BtnForm = styled.div`
    margin-top: 10px;
`;

const MyStyledTextarea = MyStyledInput.withComponent('textarea');

export default CreatePost;
